import react from "react";
import { useState } from "react";
import Button from '@mui/material/Button';
import { useEffect } from "react";



const App = ()=>{

const [num,setnum]= useState(0);
const [nums, setnums]= useState(0);
useEffect


return( 
  <>
   <Button onClick={()=>{
    setnum(num+1)
    }}>Click Me{num}</Button>


<Button onClick={()=>{
  setnums(nums+1)
}}>Click Me{nums}</Button>

    </>



)
}

export default App;
